<?php $errors=[]; if ($_SERVER["REQUEST_METHOD"]=="POST"){$email=""; $message=""; $phone_number=""; $name=""; if(isset($_REQUEST['email'])){$email=filter_var(test_input($_REQUEST['email']), FILTER_SANITIZE_EMAIL); if(!filter_var($email, FILTER_VALIDATE_EMAIL)){$errors['email']="البريد الإلكترونى غير صالح";}}else{$errors['email']="برجاء إدخال البريد الإلكترونى";}if(isset($_REQUEST['name']) && !empty($_REQUEST['name'])){$name=test_input($_REQUEST['name']);}else{$errors['name']="برجاء إدخال الإسم";}if(isset($_REQUEST['phone']) && !empty($_REQUEST['phone'])){$phone_number=$_REQUEST['phone']; if(!isDigits($phone_number)){}}else{}if(isset($_REQUEST['message']) && !empty($_REQUEST['message'])){$message=test_input($_REQUEST['message']);}else{$errors['message']="برجاء إدخال الرسالة";}if(count($errors)==0){$to="hello@Bold Doctor.co"; $subject="Contact Message"; $txt="Name : " . $name .="\n"; $txt .="Email : " . $email .="\n"; $txt .="Phone : " . $phone_number .="\n"; $txt .="Message : " . $message .="\n"; $headers="From: hello@Bold Doctor.co"; mail($to,$subject,$txt,$headers); echo "<script>window.location.href='https://boldbrand.tech/Bold Doctor/ThankYou.php';</script>"; exit; ob_end_flush();}}function isDigits(string $s, int $minDigits=11, int $maxDigits=11): bool{return preg_match('/^[0-9]{'.$minDigits.','.$maxDigits.'}\z/', $s);}function test_input($data){$data=trim($data); $data=stripslashes($data); $data=htmlspecialchars($data); return $data;}?> ?>