import React, { useContext, useState } from 'react'
import Texts from '../Texts';
import { Context } from '../Context';
import { useNavigate } from 'react-router-dom';

export default function CallUs() {

  const { lang } = useContext(Context);
  const texts = Texts[lang];
  const navigate = useNavigate();
  const [errors, setErrors] = useState([]);
  const [input, setInput] = useState({
    name: '',
    email: '',
    phone: '',
    title: '',
    message: '',
  });

  async function submit() {

    setErrors([])
    input.name == '' && setErrors(errors => [...errors, 'الاسم'])
    input.email == '' && setErrors(errors => [...errors, 'إيميل'])
    input.phone == '' && setErrors(errors => [...errors, 'الجوال'])
    input.title == '' && setErrors(errors => [...errors, 'العنوان'])
    input.message == '' && setErrors(errors => [...errors, 'الرسالة'])

    let pass =
    input.name != '' &&
    input.email != '' &&
    input.phone
    input.title
    input.message

    if (pass) {

      const formData = new FormData();
      formData.append('name', individual.name);
      formData.append('age', individual.age);
      formData.append('nationality', individual.nationality);
      formData.append('identityNo', individual.identityNo);
      formData.append('identitySource', individual.identitySource);

      fetch("https://vahpa.org.sa/server/api/individual-register", {
        method: 'POST',
        body: formData
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.status == 200) {
            alert('تم الاشتراك بنجاح ')
          }
          else {
            alert('حقل البريد الإلكتروني مستخدم من قبل')
          }
        })
    }

  }

  return (
    <>
      <div className='page-title'>
        <h1>{texts.callUs}</h1>
      </div>
      <div className='content'>
        <div className='row justify-content-center text-end'>
          <div className='col-md-6'>

            <div className='card shadow-sm'>
              <div className='card-header'>
                <h4 className='text-black'>اتصل بنا</h4>
              </div>
              <div className='card-body'>
                <input name='name' className='form-control my-4' placeholder='الإسم الثلاثي' />
                <input name='email' className='form-control my-4' placeholder='البريد الإلكتروني' />
                <input name='number' className='form-control my-4' placeholder='رقم الجوال' />
                <input name='adress' className='form-control my-4' placeholder='العنوان' />
                <textarea className='form-control my-4' placeholder='الرسالة'></textarea>

                <button className='btn btn bg-dark text-white float-start mt-5'>إرسال</button>
              </div>
            </div>
          </div>
        </div>

        <div className='row justify-content-center text-end'>
          <div className='col-md-6'>
            <button onClick={()=>{navigate('/messages')}} className='btn btn bg-dark text-white my-5'>عرض الرسائل</button>
          </div>
        </div>
      </div>
    </>
  )
}
