import React, { useContext, useEffect, useRef } from 'react'
import './App.css';
import { Route, Routes} from 'react-router-dom'
import Main from './pages/Main';
import AboutUs from './pages/AboutUs';
import logo from './images/logo.png';
import title from './images/title.png';
import logoWhite from './images/logoWhite.png';
import panner1 from './images/panner1.PNG'
import panner2 from './images/panner2.PNG'
import Texts from './Texts';
import Header from './components/Header';
import Footer from './components/Footer';
import {Context} from './Context';

function App() {

  const {lang} = useContext(Context);
  const texts = Texts[lang];
  const container = useRef();

  useEffect(() => {
    const script = document.createElement('script');

    script.src = "https://platform.twitter.com/widgets.js";
    script.async = true;

    document.body.appendChild(script);
  }, []);

  useEffect(() => {
    lang == 'ar'
    ?  container.current.style.direction = 'rtl'
    :  container.current.style.direction = 'ltr'
  },[lang])

  return (
    <div ref={container} className='container'>
      <img className='background-img' src={panner2} alt='panner'/>
      <Header />
      <Main />
      <Footer />
    </div>
  );
}
export default App;
