import React, { useContext, useEffect, useRef } from 'react'
import './App.css';
import { Route, Routes} from 'react-router-dom'
import Main from './pages/Main';
import AboutUs from './pages/AboutUs';
import logo from './images/logo.png';
import title from './images/title.png';
import Texts from './Texts';
import {Context} from './Context';

function App() {

  const {lang, setLang} = useContext(Context);
  const texts = Texts[lang];
  const container = useRef();

  // function changeLang(){
  //   if (lang == 'ar'){
  //     setLang('en')
  //     container.current.style.direction = 'ltr';
  //   }
  //   else{
  //     setLang('ar')
  //     container.current.style.direction = 'rtl';
  //   }
  // }

  return (
    <>
      <div ref={container} className='container'>
      <div className='header'>
        <div className='banner'>
          <img src={logo} className='logo-img' alt='logo'/>
          <img src={title} className='title-img' alt='title'/>
        </div>
        {/* <div className='news'>
          <p>{texts.latestNews}</p>
          <div></div>
          <p>{texts.latestNewsContent}</p>
        </div> */}
        <div className='navbar'>
          <button>{texts.mainPage}</button>
          <button>{texts.about} <i class="fa-sharp fa-solid fa-caret-down"></i></button>
          <button>{texts.services} <i class="fa-sharp fa-solid fa-caret-down"></i></button>
          <button>{texts.membership} <i class="fa-sharp fa-solid fa-caret-down"></i></button>
          <button>{texts.ourPrograms}</button>
          <button>{texts.news}</button>
          <button>{texts.library} <i class="fa-sharp fa-solid fa-caret-down"></i></button>
        </div>
      </div>
        {/* <div className='body'>
          <div className='sidebar'>
            <div className='sidebar-header'>
              <h3>{texts.mainMenu}</h3>
              <button onClick={changeLang}> {lang == 'ar' ? 'English' : 'عربي'}</button>
            </div>
            <button>{texts.mainPage}</button>
            <button>{texts.about}</button>
            <button>{texts.services}</button>
            <button>{texts.membership}</button>
            <button>{texts.ourPrograms}</button>
            <button>{texts.news}</button>
            <button>{texts.library}</button>
          </div>
          <div className='content'>
            <Routes>
              <Route path='/' element={<Main />} />
              <Route path='/about' element={<AboutUs />} />
              <Route path='/*' element={<Main />} />
            </Routes>
          </div>
        </div> */}
        <div className='footer'>
          <p>{texts.identity}</p>
          <div>
            <p>{texts.contact}</p>
              <a href='mailto: d2105944@gmail.com' target='_blank'>
                <i className="fa-solid fa-envelope"></i>
              </a>
              <a href='https://twitter.com' target='_blank'>
                <i className="fa-brands fa-twitter"></i>
              </a>
              <a href='https://www.linkedin.com/in/mohamed-alimam-474b9522a/' target='_blank'>
                <i className="fa-brands fa-linkedin"></i>
              </a>
              <a href='https://wa.me/+966576344747?text=Hi' target='_blank'>
                <i className="fa-brands fa-whatsapp"></i>
              </a>
              <a href='https://www.instagram.com' target='_blank'>
                <i className="fa-brands fa-instagram"></i>
              </a>
              <a href='https://t.me' target='_blank'>
                <i className="fa-brands fa-telegram"></i>
              </a>
              <a href='tel: +966576344747'>
                <i className="fa-solid fa-phone"></i>
              </a>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
