import React, { useContext, useEffect, useRef, useState } from 'react'
import { Context } from '../Context';
import Texts from '../Texts';
import logoWhite from '../images/logoWhite.png';
import panner1 from '../images/panner1.PNG'
import panner2 from '../images/panner2.PNG'

export default function Main() {

  const { lang } = useContext(Context);
  const texts = Texts[lang];
  const [activeImg, setActiveImg] = useState('img1');

  useEffect(() => {
    const script = document.createElement('script');

    script.src = "https://platform.twitter.com/widgets.js";
    script.async = true;

    document.body.appendChild(script);
  }, []);

  let slideIndex = 1;
  // showSlides(slideIndex);

  // Next/previous controls
  // function plusSlides(n) {
  //   showSlides(slideIndex += n);
  // }

  // Thumbnail image controls
  // function currentSlide(n) {
  //   showSlides(slideIndex = n);
  // }

  // function showSlides(n) {
  //   let i;
  //   let slides = document.getElementsByClassName("mySlides");
  //   let dots = document.getElementsByClassName("dot");
  //   if (n > slides.length) { slideIndex = 1 }
  //   if (n < 1) { slideIndex = slides.length }
  //   for (i = 0; i < slides.length; i++) {
  //     slides[i].style.display = "none";
  //   }
  //   for (i = 0; i < dots.length; i++) {
  //     dots[i].className = dots[i].className.replace(" active", "");
  //   }
  //   slides[slideIndex - 1].style.display = "block";
  //   dots[slideIndex - 1].classList.add(" active");
  // }

  return (
    <div className='body'>
      <img className='background-img' src={panner2} alt='panner' />
      {/* <img src={panner1} alt='panner' /> */}
      {/*Slideshow container*/}
      <div className="slideshow-container">
        <div className={`mySlides fade ${activeImg == "img1" && 'showSlide'}`}>
          <img src={panner1} className='slide-img' />
        </div>
        <div className={`mySlides fade ${activeImg == "img2" && 'showSlide'}`}>
          <img src={panner2} className='slide-img' />
        </div>
      </div>
      <div className='dot-container'>
        <span className={`dot ${activeImg == "img1" && 'activeDot'}`} onClick={() => { setActiveImg('img1') }}></span>
        <span className={`dot ${activeImg == "img2" && 'activeDot'}`} onClick={() => { setActiveImg('img2') }}></span>
      </div>
      <div className='news-container'>
        <h2>{texts.latestNews}</h2>
        <div className='post-container'>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
          <div className='post'>
            <img src={logoWhite} alt='img' />
            <div>
              <h1>23</h1>
              <p>2022 Oct</p>
            </div>
            <section></section>
            <p>{texts.post}</p>
          </div>
        </div>
      </div>
      <div className="twitter-container">
        <a className="twitter-timeline" data-width="530" data-height="400" href="https://twitter.com/VAHPA_SA?ref_src=twsrc%5Etfw">Tweets by VAHPA_SA</a>
      </div>
    </div>
  )
}
