import React, { useContext, useEffect, useState } from 'react'
import { Context } from '../Context';
import Texts from '../Texts';
import { useNavigate } from 'react-router-dom'

export default function Header() {

    const { lang, setLang } = useContext(Context);

    useEffect(()=>{
        console.log(lang)
    },[lang])
    const texts = Texts["en"];
    const navigate = useNavigate();

    function changeLang(){
        if (lang == 'en') setLang('ar');
        else setLang('en');
    }

    return (
        <div className='header'>
            <h3 onClick={()=> navigate("/")}>{texts.name}</h3>
            <ul>
                <li onClick={()=> navigate("/")}>{texts.home}</li>
                <li onClick={()=> navigate("/about-us")}>{texts.about}</li>
                <li onClick={()=> navigate("/contact-us")}>{texts.contact}</li>
                <li onClick={changeLang}>{texts.lang}</li>
            </ul>
        </div>
    )
}
