import React, { useContext } from 'react'
import { Context } from '../Context';
import Texts from '../Texts';

export default function ContactUs() {

  const { lang, setLang } = useContext(Context);
  const texts = Texts[lang];

  return (
    <div className='card-container up contact-container'>
      <div className='card shadow-sm'>
        <div className='card-header'>
          <h4 className='text-black'>{texts.contact}</h4>
        </div>
        <div className='card-body'>
          <input name='name' className='form-control my-4' placeholder={texts.name} />
          <input name='email' className='form-control my-4' placeholder={texts.email} />
          <input name='phone' className='form-control my-4' placeholder={texts.phone} />
          <textarea className='form-control my-4' placeholder={texts.message} ></textarea>
          <p>{texts.address}</p>
          <p>{texts.email2}<a href='mailto: sultan@althaaly.com' className='px-1'>{texts.sultanEmail}</a></p>
          <p>{texts.telephone}<a href='tel: +966114623362' target='_blank' className='px-1'>{texts.sultanTelephone}</a></p>
          <button className='btn btn bg-primary text-white float-start mt-4'>{texts.send}</button>
        </div>
      </div>
    </div>
  )
}
