import React, { useEffect, useState } from 'react'
import Texts from '../Texts';
import { useNavigate } from 'react-router-dom'

export default function Header() {

    const [ lang, setLang ] = useState('en');
    const texts = Texts[lang];
    const navigate = useNavigate();

    function changeLang(){
        if (lang == 'en') setLang('ar');
        else setLang('en');
    }

    return (
        <div className='header'>
            <h3 onClick={()=> navigate("/")}></h3>
            <ul>
                <li onClick={()=> navigate("/")}>{texts.home}</li>
                <li onClick={()=> navigate("/about-us")}>{texts.about}</li>
                <li onClick={()=> navigate("/contact-us")}>{texts.contact}</li>
                <li onClick={changeLang}>{texts.lang}</li>
            </ul>
        </div>
    )
}
