import React, { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Context } from '../Context';
import Texts from '../Texts';

export default function Header() {

    const { lang, setLang } = useContext(Context);
    const texts = Texts[lang];
    const navigate = useNavigate();

    function changeLang(){
        if (lang == 'en') setLang('ar');
        else setLang('en');
    }

    return (
        <div className='header'>
            <h3 onClick={()=> navigate("/")}>{texts.logo}</h3>
            <ul>
                <li onClick={()=> navigate("/")}>{texts.home}</li>
                <li onClick={()=> navigate("/about-us")}>{texts.about}</li>
                <li onClick={()=> navigate("/cv")}>{texts.cv}</li>
                <li onClick={()=> navigate("/contact-us")}>{texts.contact}</li>
                <li onClick={changeLang}>{texts.lang}</li>
            </ul>
        </div>
    )
}
