export const Context = React.createContext({});

export default function ContextProvider({ children }) {

  const [ lang, setLang ] = useState("en");

  useEffect(()=>{
    console.log(lang)
},[lang])

  return (
    <Context.Provider value={{ lang, setLang }}>
      {children}
    </Context.Provider>
  )
}
