import React, { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Context } from '../Context';
import Texts from '../Texts';

export default function Header() {

    const [showSidebar, setShowSidebar] = useState(false);
    const { lang, setLang } = useContext(Context);
    const texts = Texts[lang];
    const navigate = useNavigate();

    function changeLang(){
        if (lang == 'en') setLang('ar');
        else setLang('en');
    }

    function navigateUrl(page){
        setShowSidebar(false)
        navigate(`/${page}`)
    }

    return (
        <div className='header'>
            <h3 onClick={()=> navigateUrl("")}>{texts.logo}</h3>
            <i className="fa-solid fa-bars" onClick={()=> setShowSidebar(true)}></i>
            <ul className={`links-container ${showSidebar ? 'showSidebar' : ''}`}>
                <li onClick={()=> navigateUrl("")}>{texts.home}</li>
                <li onClick={()=> navigateUrl("about-us")}>{texts.about}</li>
                <li onClick={()=> navigateUrl("cv")}>{texts.cv}</li>
                <li onClick={()=> navigateUrl("contact-us")}>{texts.contact}</li>
                <li onClick={changeLang}>{texts.lang}</li>
            </ul>
        </div>
    )
}
