import React, { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Context } from '../Context';
import Texts from '../Texts';

export default function Header() {

    const [showSidebar, setShowSidebar] = useState(false);
    const { lang, setLang, setPage } = useContext(Context);
    const texts = Texts[lang];
    const navigate = useNavigate();

    function changeLang(){
        setShowSidebar(false)
        if (lang == 'en') setLang('ar');
        else setLang('en');
    }

    // function navigateUrl(page){
    //     setShowSidebar(false)
    //     navigate(`/${page}`)
    // }

    return (
        <div className='header'>
            <h3 onClick={()=> setPage("landing")}>{texts.logo}</h3>
            <i className="fa-solid fa-bars" onClick={()=> setShowSidebar(true)}></i>
            <ul className={`links-container ${showSidebar && 'showSidebar'}`}>
                <li onClick={()=> setPage("landing")}>{texts.home}</li>
                <li onClick={()=> setPage("cv")}>{texts.cv}</li>
                <li onClick={()=> setPage("contact")}>{texts.contact}</li>
                <li onClick={changeLang}>{texts.lang}</li>
            </ul>
        </div>
    )
}
